import React, { useState, FormEvent } from 'react';
import laboratorioApi from '../api/laboratorioApi';

const CrearRequisicionForm = () => {
  const [chainsawId, setChainsawId] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [observation, setObservation] = useState('');
  const [requisitionDate, setRequisitionDate] = useState(new Date().toISOString().slice(0, 16));
  const [mensaje, setMensaje] = useState('');

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();

    try {
      const body = {
        chainsawId,
        quantity,
        requisitionDate,
        observation,
      };

      const res = await laboratorioApi.post('/performRequisition/create', body);
      console.log('Requisición creada:', res.data);
      setMensaje('✅ Requisición creada correctamente');
    } catch (error) {
      console.error('Error al crear requisición:', error);
      setMensaje('❌ Error al crear requisición');
    }
  };

  return (
    <div className="max-w-md mx-auto bg-white shadow-lg rounded-xl p-6 mt-10">
      <h2 className="text-2xl font-semibold mb-4 text-center text-blue-600">Crear Requisición</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700">ID de Chainsaw</label>
          <input
            type="text"
            value={chainsawId}
            onChange={(e) => setChainsawId(e.target.value)}
            required
            className="mt-1 w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring focus:ring-blue-200"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">Cantidad</label>
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(Number(e.target.value))}
            required
            className="mt-1 w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring focus:ring-blue-200"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">Fecha de Requisición</label>
          <input
            type="datetime-local"
            value={requisitionDate}
            onChange={(e) => setRequisitionDate(e.target.value)}
            required
            className="mt-1 w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring focus:ring-blue-200"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">Observación</label>
          <textarea
            value={observation}
            onChange={(e) => setObservation(e.target.value)}
            className="mt-1 w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring focus:ring-blue-200"
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white font-semibold py-2 px-4 rounded-md hover:bg-blue-700 transition"
        >
          Crear
        </button>

        {mensaje && (
          <p
            className={`text-sm mt-2 ${
              mensaje.includes('✅') ? 'text-green-600' : 'text-red-600'
            }`}
          >
            {mensaje}
          </p>
        )}
      </form>
    </div>
  );
};

export default CrearRequisicionForm;
